package org.example.controller;

import org.example.dto.RecognitionResponse;
import org.example.dto.StatusUpdateRequest;
import org.example.model.Recognition;
import org.example.model.Employee;
import org.example.repository.EmployeeRepository;
import org.example.repository.RecognitionRepository;
import org.example.service.RecognitionCsvExporter;
import org.example.util.EntityMapper;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.PageRequest;
import org.springframework.data.domain.Pageable;
import org.springframework.http.HttpHeaders;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;
import org.springframework.web.servlet.mvc.method.annotation.StreamingResponseBody;

import java.io.OutputStream;
import java.util.List;
import java.util.Optional;
import java.util.UUID;
import java.util.stream.Collectors;

@RestController
@RequestMapping("/api/v1/recognitions")
public class RecognitionV1Controller {

    private final RecognitionRepository recognitionRepository;
    private final EmployeeRepository employeeRepository;
    private final RecognitionCsvExporter csvExporter;

    public RecognitionV1Controller(RecognitionRepository recognitionRepository, EmployeeRepository employeeRepository, RecognitionCsvExporter csvExporter) {
        this.recognitionRepository = recognitionRepository;
        this.employeeRepository = employeeRepository;
        this.csvExporter = csvExporter;
    }

    @GetMapping
    public Page<RecognitionResponse> list(@RequestParam(defaultValue = "0") int page,
                                         @RequestParam(defaultValue = "20") int size,
                                         @RequestParam(required = false) Long senderId,
                                         @RequestParam(required = false) UUID senderUuid,
                                         @RequestParam(required = false) Long recipientId,
                                         @RequestParam(required = false) UUID recipientUuid) {
        Pageable p = PageRequest.of(page, size);

        // prefer uuid if provided
        if (senderUuid != null) {
            Optional<java.util.UUID> su = Optional.of(senderUuid);
            List<Employee> senders = employeeRepository.findAllByUuidIn(List.of(senderUuid)).stream().collect(Collectors.toList());
            if (!senders.isEmpty()) {
                return recognitionRepository.findAllBySenderId(senders.get(0).getId(), p).map(EntityMapper::toRecognitionResponse);
            }
        }
        if (recipientUuid != null) {
            List<Employee> recs = employeeRepository.findAllByUuidIn(List.of(recipientUuid)).stream().collect(Collectors.toList());
            if (!recs.isEmpty()) {
                return recognitionRepository.findAllByRecipientId(recs.get(0).getId(), p).map(EntityMapper::toRecognitionResponse);
            }
        }

        if (senderId != null) return recognitionRepository.findAllBySenderId(senderId, p).map(EntityMapper::toRecognitionResponse);
        if (recipientId != null) return recognitionRepository.findAllByRecipientId(recipientId, p).map(EntityMapper::toRecognitionResponse);

        return recognitionRepository.findAllWithRelations(p).map(EntityMapper::toRecognitionResponse);
    }

    @GetMapping("/{uuid}")
    public ResponseEntity<?> getByUuid(@PathVariable UUID uuid) {
        Optional<Recognition> r = recognitionRepository.findByUuidWithRelations(uuid);
        return r.map(rec -> ResponseEntity.ok(EntityMapper.toRecognitionResponse(rec))).orElseGet(() -> ResponseEntity.notFound().build());
    }

    // backward-compatible: allow id query
    @GetMapping(params = "id")
    public ResponseEntity<?> getById(@RequestParam Long id) {
        Optional<Recognition> r = recognitionRepository.findByIdWithRelations(id);
        return r.map(rec -> ResponseEntity.ok(EntityMapper.toRecognitionResponse(rec))).orElseGet(() -> ResponseEntity.notFound().build());
    }

    @PatchMapping("/{uuid}/status")
    public ResponseEntity<?> updateStatus(@PathVariable UUID uuid, @RequestBody StatusUpdateRequest req) {
        Optional<Recognition> opt = recognitionRepository.findByUuidWithRelations(uuid);
        if (opt.isEmpty()) return ResponseEntity.notFound().build();
        Recognition r = opt.get();
        String desired = req != null ? req.status : null;
        if (desired == null) return ResponseEntity.badRequest().body(java.util.Map.of("error", "status is required"));
        String upper = desired.toUpperCase();
        if ("APPROVED".equals(upper)) {
            r.setApprovalStatus("APPROVED");
            r.setRejectionReason(null); // clear rejectionReason on approve
        } else if ("REJECTED".equals(upper)) {
            if (req.rejectionReason == null || req.rejectionReason.isBlank()) return ResponseEntity.badRequest().body(java.util.Map.of("error", "rejectionReason required for REJECTED status"));
            r.setApprovalStatus("REJECTED");
            r.setRejectionReason(req.rejectionReason);
        } else if ("PENDING".equals(upper)) {
            r.setApprovalStatus("PENDING");
        } else {
            return ResponseEntity.badRequest().body(java.util.Map.of("error", "unknown status"));
        }
        recognitionRepository.save(r);
        Recognition reloaded = recognitionRepository.findByUuidWithRelations(r.getUuid()).orElse(r);
        return ResponseEntity.ok(EntityMapper.toRecognitionResponse(reloaded));
    }

    @GetMapping("/export")
    public ResponseEntity<?> export(@RequestParam(defaultValue = "json") String format,
                                    @RequestParam(defaultValue = "false") boolean stream,
                                    @RequestParam(required = false) UUID recipientUuid,
                                    @RequestParam(required = false) Long recipientId) throws Exception {
        List<Recognition> list;
        if (recipientUuid != null) {
            List<Employee> recs = employeeRepository.findAllByUuidIn(List.of(recipientUuid));
            if (!recs.isEmpty()) list = recognitionRepository.findAllByRecipientId(recs.get(0).getId(), Pageable.unpaged()).getContent();
            else list = List.of();
        } else if (recipientId != null) {
            list = recognitionRepository.findAllByRecipientId(recipientId, Pageable.unpaged()).getContent();
        } else {
            list = recognitionRepository.findAllWithRelations(Pageable.unpaged()).getContent();
        }

        if ("csv".equalsIgnoreCase(format)) {
            if (stream) {
                StreamingResponseBody s = (OutputStream os) -> {
                    try {
                        csvExporter.exportToStream(os, list);
                    } catch (Exception ex) {
                        throw new RuntimeException(ex);
                    }
                };
                return ResponseEntity.ok()
                        .header(HttpHeaders.CONTENT_DISPOSITION, "attachment; filename=recognitions.csv")
                        .contentType(MediaType.parseMediaType("text/csv"))
                        .body(s);
            } else {
                byte[] bytes = csvExporter.export(list);
                return ResponseEntity.ok()
                        .header(HttpHeaders.CONTENT_DISPOSITION, "attachment; filename=recognitions.csv")
                        .contentType(MediaType.parseMediaType("text/csv"))
                        .body(bytes);
            }
        } else {
            List<RecognitionResponse> resp = list.stream().map(EntityMapper::toRecognitionResponse).collect(Collectors.toList());
            return ResponseEntity.ok(resp);
        }
    }
}

