import React from 'react';

function getUserRoles(): string[] {
  const token = localStorage.getItem('token');
  if (!token) return [];
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.roles || payload.role || [];
  } catch {
    return [];
  }
}

const MetricsPage: React.FC = () => {
  const roles = getUserRoles();
  const canView = roles.includes('ADMIN') || roles.includes('MANAGER') || roles.includes('TEAMLEADER');

  return (
    <div style={{ width: '100vw', minHeight: '100vh', display: 'block' }}>
      <div style={{ maxWidth: 850, width: '100%', padding: 10, background: '#f5f7fa', borderRadius: 12, boxShadow: '0 4px 16px #e0e0e0', display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '32px 0 0 32px' }}>
        <h2 style={{ textAlign: 'center', marginBottom: 18, fontSize: '1.3rem', fontWeight: 600 }}>Metrics</h2>
        {canView ? (
          <p style={{ fontSize: '0.7rem' }}>Metrics and graphs available for your role.</p>
        ) : (
          <p style={{ color: 'red', fontSize: '0.7rem' }}>You do not have permission to view metrics.</p>
        )}
        {/* Metrics content will go here */}
      </div>
    </div>
  );
};

export default MetricsPage;
