import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RecognitionManagement: React.FC = () => {
  const [recognitions, setRecognitions] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRecognitions = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/recognitions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setRecognitions(res.data.content || []);
      } catch (err: any) {
        setError('Failed to fetch recognitions');
      } finally {
        setLoading(false);
      }
    };
    fetchRecognitions();
  }, []);

  return (
    <div style={{ width: '100vw', minHeight: '100vh', display: 'block' }}>
      <div style={{ maxWidth: 850, width: '100%', padding: 10, background: '#f5f7fa', borderRadius: 12, boxShadow: '0 4px 16px #e0e0e0', display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '32px 0 0 32px' }}>
        <h2 style={{ textAlign: 'center', marginBottom: 18, fontSize: '1.3rem', fontWeight: 600 }}>Recognition Management</h2>
        {loading && <div style={{ fontSize: '0.7rem' }}>Loading recognitions...</div>}
        {error && <div style={{ color: 'red', fontSize: '0.7rem', marginBottom: 8 }}>{error}</div>}
        <ul style={{ fontSize: '0.7rem', width: '100%', padding: 0, margin: 0, listStyle: 'none' }}>
          {recognitions.map(rec => (
            <li key={rec.id} style={{ padding: '6px 0', borderBottom: '1px solid #e0e0e0', color: '#333' }}>
              <span style={{ fontWeight: 500 }}>{rec.category}</span> - <span>{rec.level}</span> (<span style={{ color: '#1976d2', fontWeight: 600 }}>{rec.awardPoints} pts</span>)
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RecognitionManagement;
