import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../services/auth';
import { PAGE_PERMISSIONS, Role } from '../services/permissions';

const Sidebar: React.FC = () => {
  const { user } = useAuth();
  // User info display
  const name = user?.email?.split('@')[0] || 'User';
  const role = user?.role ? user.role.charAt(0).toUpperCase() + user.role.slice(1) : '';

  // Role-based navigation using permissions utility
  let navLinks = [
    { to: '/', label: 'Dashboard', page: 'dashboard' },
    { to: '/leaderboard', label: 'Leaderboard', page: 'leaderboard' },
  ];
  if (user) {
    if (user.role === 'employee') {
      navLinks.push({ to: '/my-recognitions', label: 'My Recognitions', page: 'recognitions' });
    } else if (user.role === 'teamlead') {
      navLinks.push(
        { to: '/my-dashboard', label: 'My Dashboard', page: 'dashboard' },
        { to: '/my-recognitions', label: 'My Recognitions', page: 'recognitions' },
        { to: '/team-dashboard', label: 'Team Dashboard', page: 'dashboard' },
        { to: '/team-recognitions', label: 'Team Recognitions', page: 'recognitions' }
      );
    } else if (user.role === 'manager') {
      navLinks.push(
        { to: '/my-dashboard', label: 'My Dashboard', page: 'dashboard' },
        { to: '/my-recognitions', label: 'My Recognitions', page: 'recognitions' },
        { to: '/unit-dashboard', label: 'Unit Dashboard', page: 'dashboard' },
        { to: '/unit-recognitions', label: 'Unit Recognitions', page: 'recognitions' },
        { to: '/employees', label: 'Employees', page: 'employees' }
      );
    } else if (user.role === 'admin') {
      navLinks = [
        { to: '/', label: 'Dashboard', page: 'dashboard' },
        { to: '/leaderboard', label: 'Leaderboard', page: 'leaderboard' },
        { to: '/employees', label: 'Employees', page: 'employees' },
        { to: '/recognitions', label: 'Recognitions', page: 'recognitions' },
        { to: '/recognition-types', label: 'Recognition Types', page: 'recognitionTypes' },
      ];
    }
  }

  return (
    <div style={{ width: 180, minHeight: '100vh', background: '#8da1bd', color: '#fff', display: 'flex', flexDirection: 'column', alignItems: 'flex-start', padding: '24px 0 0 16px', position: 'fixed', left: 0, top: 0, zIndex: 100 }}>
      <div style={{ marginBottom: 28, width: '100%' }}>
        <div style={{ fontWeight: 700, fontSize: '1.05rem', marginBottom: 2 }}>{name}</div>
        <div style={{ fontSize: '0.95rem', color: '#e0e0e0' }}>{role}</div>
        <hr style={{ border: 0, borderTop: '1px solid #bfcbe3', margin: '16px 0 0 0', width: '90%' }} />
      </div>
      <h3 style={{ marginBottom: 24, fontSize: '1.1rem', fontWeight: 700 }}>Navigation</h3>
      <nav style={{ display: 'flex', flexDirection: 'column', gap: 18 }}>
        {navLinks.map(link => (
          <Link key={link.to} to={link.to} style={{ color: '#fff', textDecoration: 'none', fontSize: '0.95rem', fontWeight: 500 }}>{link.label}</Link>
        ))}
      </nav>
    </div>
  );
};

export default Sidebar;
