import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PAGE_SIZE_OPTIONS = [10, 20, 50];

const RecognitionManagement: React.FC = () => {
  const [recognitions, setRecognitions] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(0);
  const [size, setSize] = useState(PAGE_SIZE_OPTIONS[0]);
  const [totalPages, setTotalPages] = useState(1);
  const [sortField, setSortField] = useState('date');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');

  const sortRecognitions = (data: any[]) => {
    return [...data].sort((a, b) => {
      let aVal, bVal;
      switch (sortField) {
        case 'category': aVal = a.category || ''; bVal = b.category || ''; break;
        case 'points': aVal = a.awardPoints || 0; bVal = b.awardPoints || 0; break;
        case 'sender': aVal = a.senderName || a.sender || ''; bVal = b.senderName || b.sender || ''; break;
        case 'recipient': aVal = a.recipientName || a.recipient || ''; bVal = b.recipientName || b.recipient || ''; break;
        case 'date': aVal = a.sentAt || a.createdAt || 0; bVal = b.sentAt || b.createdAt || 0; break;
        default: aVal = a.id; bVal = b.id;
      }
      if (aVal < bVal) return sortOrder === 'asc' ? -1 : 1;
      if (aVal > bVal) return sortOrder === 'asc' ? 1 : -1;
      return 0;
    });
  };

  const fetchRecognitions = async (newPage = page, newSize = size) => {
    setLoading(true);
    setError('');
    try {
      // Enforce limits
      if (newSize < 1) newSize = 1;
      if (newSize > 100) newSize = 100;
      const res = await axios.get(`/recognitions?page=${newPage}&size=${newSize}`);
      const data = res.data;
      let items = data.content || [];
      items = sortRecognitions(items);
      setRecognitions(items);
      setTotalPages(data.totalPages || 1);
      setPage(data.pageable?.pageNumber ?? newPage);
      setSize(data.pageable?.pageSize ?? newSize);
    } catch (err: any) {
      setError('Failed to fetch recognitions from the backend. Please check if the backend is running and the endpoint is correct.');
      setRecognitions([]);
      setTotalPages(1);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRecognitions();
  }, []);

  return (
    <div style={{ width: '100%', overflowX: 'auto' }}>
      <h3 style={{ textAlign: 'center', fontSize: '1rem', fontWeight: 600, marginBottom: 12 }}>Recognitions List</h3>
      {loading && <div style={{ fontSize: '0.7rem' }}>Loading recognitions...</div>}
      {error && <div style={{ color: 'red', fontSize: '0.7rem', marginBottom: 8 }}>{error}</div>}
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 10 }}>
        <div>
          <label style={{ fontSize: '0.7rem', marginRight: 8 }}>Sort by:</label>
          <select value={sortField} onChange={e => { setSortField(e.target.value); fetchRecognitions(page, size); }} style={{ fontSize: '0.7rem', marginRight: 8 }}>
            <option value="date">Date</option>
            <option value="category">Category</option>
            <option value="points">Points</option>
            <option value="sender">Sender</option>
            <option value="recipient">Recipient</option>
          </select>
          <select value={sortOrder} onChange={e => { setSortOrder(e.target.value as 'asc' | 'desc'); fetchRecognitions(page, size); }} style={{ fontSize: '0.7rem' }}>
            <option value="desc">Descending</option>
            <option value="asc">Ascending</option>
          </select>
        </div>
      </div>
      <table style={{ width: '100%', fontSize: '0.7rem', background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0' }}>
        <thead>
          <tr style={{ background: '#8da1bd' }}>
            <th style={{ padding: 8, cursor: 'pointer' }} onClick={() => {
              if (sortField === 'category') setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
              setSortField('category');
              fetchRecognitions(page, size);
            }}>
              Category {sortField === 'category' && (sortOrder === 'asc' ? '▲' : '▼')}
            </th>
            <th style={{ padding: 8, cursor: 'pointer' }} onClick={() => {
              if (sortField === 'level') setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
              setSortField('level');
              fetchRecognitions(page, size);
            }}>
              Level {sortField === 'level' && (sortOrder === 'asc' ? '▲' : '▼')}
            </th>
            <th style={{ padding: 8, cursor: 'pointer' }} onClick={() => {
              if (sortField === 'points') setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
              setSortField('points');
              fetchRecognitions(page, size);
            }}>
              Points {sortField === 'points' && (sortOrder === 'asc' ? '▲' : '▼')}
            </th>
            <th style={{ padding: 8, cursor: 'pointer' }} onClick={() => {
              if (sortField === 'sender') setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
              setSortField('sender');
              fetchRecognitions(page, size);
            }}>
              Sender {sortField === 'sender' && (sortOrder === 'asc' ? '▲' : '▼')}
            </th>
            <th style={{ padding: 8, cursor: 'pointer' }} onClick={() => {
              if (sortField === 'recipient') setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
              setSortField('recipient');
              fetchRecognitions(page, size);
            }}>
              Recipient {sortField === 'recipient' && (sortOrder === 'asc' ? '▲' : '▼')}
            </th>
            <th style={{ padding: 8, cursor: 'pointer' }} onClick={() => {
              if (sortField === 'date') setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
              setSortField('date');
              fetchRecognitions(page, size);
            }}>
              Date {sortField === 'date' && (sortOrder === 'asc' ? '▲' : '▼')}
            </th>
          </tr>
        </thead>
        <tbody>
          {recognitions.map(rec => (
            <tr key={rec.id} style={{ background: '#fff' }}>
              <td style={{ padding: 8 }}>{rec.category}</td>
              <td style={{ padding: 8 }}>{rec.level}</td>
              <td style={{ padding: 8 }}>{rec.awardPoints}</td>
              <td style={{ padding: 8 }}>{rec.senderName || rec.sender || '-'}</td>
              <td style={{ padding: 8 }}>{rec.recipientName || rec.recipient || '-'}</td>
              <td style={{ padding: 8 }}>{rec.sentAt || rec.createdAt || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {!loading && !error && recognitions.length === 0 && (
        <div style={{ fontSize: '0.7rem', color: '#888', marginTop: 12 }}>No recognitions found.</div>
      )}
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: 16, gap: 12 }}>
        <button style={{ fontSize: '0.7rem', padding: '4px 10px' }} disabled={page === 0} onClick={() => fetchRecognitions(0, size)}>First</button>
        <button style={{ fontSize: '0.7rem', padding: '4px 10px' }} disabled={page === 0} onClick={() => fetchRecognitions(page - 1, size)}>Prev</button>
        {/* Page number range navigation */}
        {Array.from({ length: totalPages }, (_, i) => i).map(i => {
          if (totalPages > 7 && Math.abs(i - page) > 2 && i !== 0 && i !== totalPages - 1) return null;
          return (
            <button
              key={i}
              style={{ fontSize: '0.7rem', padding: '2px 8px', margin: '0 2px', background: i === page ? '#8da1bd' : '#fff', border: i === page ? '1px solid #8da1bd' : '1px solid #ccc', borderRadius: 4 }}
              disabled={i === page}
              onClick={() => fetchRecognitions(i, size)}
            >
              {i + 1}
            </button>
          );
        })}
        <button style={{ fontSize: '0.7rem', padding: '4px 10px' }} disabled={page + 1 >= totalPages} onClick={() => fetchRecognitions(page + 1, size)}>Next</button>
        <button style={{ fontSize: '0.7rem', padding: '4px 10px' }} disabled={page + 1 >= totalPages} onClick={() => fetchRecognitions(totalPages - 1, size)}>Last</button>
        <span style={{ fontSize: '0.7rem', marginLeft: 16 }}>Page size:</span>
        <input
          type="number"
          min={1}
          max={100}
          value={size}
          onChange={e => {
            let val = Number(e.target.value);
            if (val < 1) val = 1;
            if (val > 100) val = 100;
            fetchRecognitions(0, val);
          }}
          style={{ width: 60, fontSize: '0.7rem', textAlign: 'center' }}
        />
      </div>
    </div>
  );
};

export default RecognitionManagement;
