import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../services/auth';

const MyDashboard: React.FC = () => {
  const { user } = useAuth();
  const [metrics, setMetrics] = useState({
    totalRecognitions: 0,
    topSender: '-',
    topReceiver: '-',
  });
  const [recentRecognitions, setRecentRecognitions] = useState<any[]>([]);
  const [graphData, setGraphData] = useState<any[]>([]);

  useEffect(() => {
    if (!user) return;
    // Fetch recognitions where user is sender or recipient
    axios.get(`/recognitions?page=0&size=1000`).then(res => {
      const data = Array.isArray(res.data) ? res.data : (res.data.content || []);
      const myRecs = data.filter((rec: any) => rec.senderEmail === user.email || rec.recipientEmail === user.email);
      setMetrics({
        totalRecognitions: myRecs.length,
        topSender: user.email,
        topReceiver: user.email,
      });
      setRecentRecognitions(myRecs.slice(0, 10));
      // Graph data: aggregate by month
      const monthMap: { [key: string]: number } = {};
      myRecs.forEach((rec: any) => {
        if (!rec.sentAt) return;
        const date = new Date(rec.sentAt * 1000);
        const key = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
        monthMap[key] = (monthMap[key] || 0) + 1;
      });
      const sortedKeys = Object.keys(monthMap).sort((a, b) => a.localeCompare(b));
      const last12 = sortedKeys.slice(-12);
      setGraphData(last12.map(key => ({ month: key, recognitions: monthMap[key] })));
    });
  }, [user]);

  return (
    <div style={{ padding: 24 }}>
      <h2>My Dashboard</h2>
      <div>Total Recognitions: {metrics.totalRecognitions}</div>
      <h3>Recent Recognitions</h3>
      <ul>
        {recentRecognitions.map((rec, i) => (
          <li key={i}>{rec.senderName} â†’ {rec.recipientName}: {rec.message}</li>
        ))}
      </ul>
      <h3>Recognitions Over Time</h3>
      <ul>
        {graphData.map((d, i) => (
          <li key={i}>{d.month}: {d.recognitions}</li>
        ))}
      </ul>
    </div>
  );
};

export default MyDashboard;
