import React from 'react';
import MainLayout from '../components/MainLayout';

function getUserRoles(): string[] {
  const token = localStorage.getItem('token');
  if (!token) return [];
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.roles || payload.role || [];
  } catch {
    return [];
  }
}

const MetricsPage: React.FC = () => {
  const roles = getUserRoles();
  const canView = roles.includes('ADMIN') || roles.includes('MANAGER') || roles.includes('TEAMLEADER');

  return (
    <MainLayout>
      <h2>Metrics</h2>
      {canView ? (
        <p>Metrics and graphs available for your role.</p>
      ) : (
        <p style={{ color: 'red' }}>You do not have permission to view metrics.</p>
      )}
      {/* Metrics content will go here */}
    </MainLayout>
  );
};

export default MetricsPage;
