import React from 'react';
import MainLayout from '../components/MainLayout';
import { Typography, Box, Card, CardContent, Grid } from '@mui/material';
import EmployeeManagement from '../features/EmployeeManagement';
import RecognitionManagement from '../features/RecognitionManagement';
import RecognitionTypeManagement from '../features/RecognitionTypeManagement';

function getUserRoles(): string[] {
  const token = localStorage.getItem('token');
  if (!token) return [];
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.roles || payload.role || [];
  } catch {
    return [];
  }
}

const featureMap: Record<string, string[]> = {
  ADMIN: [
    'Manage all employees',
    'Manage all recognitions',
    'Manage recognition types',
    'View metrics and graphs',
    'Export data',
    'Admin controls'
  ],
  MANAGER: [
    'Manage employees in your unit',
    'Manage recognitions in your unit',
    'Manage recognition types',
    'View metrics and graphs',
    'Export data'
  ],
  TEAMLEADER: [
    'Manage employees in your team',
    'Manage recognitions in your team',
    'View metrics and graphs',
    'Export data'
  ],
  EMPLOYEE: [
    'Send recognitions',
    'View your recognitions',
    'View leaderboard'
  ]
};

const Dashboard: React.FC = () => {
  const roles = getUserRoles();
  return (
    <MainLayout>
      <Box p={4}>
        <Typography variant="h4" gutterBottom>Welcome to MyTeam Admin Dashboard</Typography>
        <Grid container spacing={3}>
          {roles.map(role => (
            <Grid item xs={12} md={6} lg={4} key={role}>
              <Card>
                <CardContent>
                  <Typography variant="h6" color="primary" gutterBottom>
                    {role.charAt(0) + role.slice(1).toLowerCase()} Features
                  </Typography>
                  <ul>
                    {(featureMap[role] || []).map((feature, idx) => (
                      <li key={idx}>{feature}</li>
                    ))}
                  </ul>
                  {/* Render feature components based on role */}
                  {role === 'ADMIN' && <EmployeeManagement />}
                  {(role === 'ADMIN' || role === 'MANAGER' || role === 'TEAMLEADER') && <RecognitionManagement />}
                  {(role === 'ADMIN' || role === 'MANAGER') && <RecognitionTypeManagement />}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
        {/* Navigation and main content will go here */}
      </Box>
    </MainLayout>
  );
};

export default Dashboard;
