import React from 'react';
import { useAuth } from '../services/auth';

const MyRecognitions: React.FC = () => {
  const { user } = useAuth();
  const [sent, setSent] = React.useState<any[]>([]);
  const [received, setReceived] = React.useState<any[]>([]);

  React.useEffect(() => {
    if (!user) return;
    fetch(`/recognitions?senderEmail=${user.email}`)
      .then(res => res.json())
      .then(data => setSent(Array.isArray(data) ? data : data.content || []));
    fetch(`/recognitions?recipientEmail=${user.email}`)
      .then(res => res.json())
      .then(data => setReceived(Array.isArray(data) ? data : data.content || []));
  }, [user]);

  return (
    <div style={{ padding: 24 }}>
      <h2>Recognitions Sent By Me</h2>
      <table style={{ width: '100%', marginBottom: 32 }}>
        <thead>
          <tr><th>Recipient</th><th>Message</th><th>Points</th><th>Date</th></tr>
        </thead>
        <tbody>
          {sent.length === 0 ? <tr><td colSpan={4}>No recognitions sent.</td></tr> : sent.map((rec, i) => (
            <tr key={i}>
              <td>{rec.recipientName || '-'}</td>
              <td>{rec.message || '-'}</td>
              <td>{rec.awardPoints || '-'}</td>
              <td>{rec.sentAt ? new Date(rec.sentAt * 1000).toLocaleDateString() : '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Recognitions Received By Me</h2>
      <table style={{ width: '100%' }}>
        <thead>
          <tr><th>Sender</th><th>Message</th><th>Points</th><th>Date</th></tr>
        </thead>
        <tbody>
          {received.length === 0 ? <tr><td colSpan={4}>No recognitions received.</td></tr> : received.map((rec, i) => (
            <tr key={i}>
              <td>{rec.senderName || '-'}</td>
              <td>{rec.message || '-'}</td>
              <td>{rec.awardPoints || '-'}</td>
              <td>{rec.sentAt ? new Date(rec.sentAt * 1000).toLocaleDateString() : '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MyRecognitions;
