import React from 'react';
import MainLayout from '../components/MainLayout';
import EmployeeManagement from '../features/EmployeeManagement';

// Simple JWT decode utility to extract roles
function getUserRoles(): string[] {
  const token = localStorage.getItem('token');
  if (!token) return [];
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    // Adjust this if your JWT uses a different claim for roles
    return payload.roles || payload.role || [];
  } catch {
    return [];
  }
}

const EmployeesPage: React.FC = () => {
  const roles = getUserRoles();
  const canManage = roles.includes('ADMIN') || roles.includes('MANAGER') || roles.includes('TEAMLEADER');

  return (
    <MainLayout>
      <h2>Employees</h2>
      {canManage ? (
        <EmployeeManagement />
      ) : (
        <p style={{ color: 'red' }}>You do not have permission to view or manage employees.</p>
      )}
    </MainLayout>
  );
};

export default EmployeesPage;
