import React from 'react';
import MainLayout from '../components/MainLayout';

function getUserRoles(): string[] {
  const token = localStorage.getItem('token');
  if (!token) return [];
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.roles || payload.role || [];
  } catch {
    return [];
  }
}

const RecognitionsPage: React.FC = () => {
  const roles = getUserRoles();
  const canSend = roles.includes('EMPLOYEE');
  const canManage = roles.includes('ADMIN') || roles.includes('MANAGER') || roles.includes('TEAMLEADER');

  return (
    <MainLayout>
      <h2>Recognitions</h2>
      {canSend && <p>You can send recognitions.</p>}
      {canManage && <p>You can manage recognitions for your team/unit.</p>}
      {!canSend && !canManage && (
        <p style={{ color: 'red' }}>You do not have permission to send or manage recognitions.</p>
      )}
      {/* Table and actions for recognitions will go here */}
    </MainLayout>
  );
};

export default RecognitionsPage;
