import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RecognitionTypeManagement: React.FC = () => {
  const [types, setTypes] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTypes = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/recognition-types', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTypes(res.data || []);
      } catch (err: any) {
        setError('Failed to fetch recognition types');
      } finally {
        setLoading(false);
      }
    };
    fetchTypes();
  }, []);

  if (loading) return <div>Loading recognition types...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h3>Recognition Type Management</h3>
      <ul>
        {types.map(type => (
          <li key={type.id}>{type.typeName}</li>
        ))}
      </ul>
    </div>
  );
};

export default RecognitionTypeManagement;
