import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EmployeeManagement: React.FC = () => {
  const [employees, setEmployees] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    email: '',
    role: 'employee',
    unitId: '',
    managerId: '',
    joiningDate: ''
  });
  const [creating, setCreating] = useState(false);
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editForm, setEditForm] = useState<any>(null);
  const [editLoading, setEditLoading] = useState(false);
  const [editError, setEditError] = useState('');
  const [editSuccess, setEditSuccess] = useState('');
  const [page, setPage] = useState(0);
  const [pageSize, setPageSize] = useState(20);
  const [totalPages, setTotalPages] = useState(1);

  const fetchEmployees = async (newPage = page, newPageSize = pageSize) => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get(`/employees?page=${newPage}&size=${newPageSize}`);
      setEmployees(res.data.content || []);
      setTotalPages(res.data.totalPages || 1);
      setPage(res.data.pageable?.pageNumber ?? newPage);
      setPageSize(res.data.pageable?.pageSize ?? newPageSize);
    } catch (err: any) {
      setError('Failed to fetch employees');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement> | React.ChangeEvent<HTMLSelectElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCreate = async (e: React.FormEvent) => {
    e.preventDefault();
    setCreating(true);
    setError('');
    setSuccess('');
    try {
      await axios.post('/employees', form);
      setSuccess('Employee created successfully');
      setForm({ firstName: '', lastName: '', email: '', role: 'employee', unitId: '', managerId: '', joiningDate: '' });
      fetchEmployees();
    } catch (err: any) {
      setError('Failed to create employee');
    } finally {
      setCreating(false);
    }
  };

  const handleDelete = async (id: number) => {
    setError('');
    setSuccess('');
    try {
      await axios.delete(`/employees/single?id=${id}`);
      setSuccess('Employee deleted successfully');
      fetchEmployees();
    } catch (err: any) {
      setError('Failed to delete employee');
    }
  };

  const startEdit = (emp: any) => {
    setEditingId(emp.id);
    setEditForm({
      firstName: emp.firstName || '',
      lastName: emp.lastName || '',
      email: emp.email || '',
      role: emp.role || 'employee',
      unitId: emp.unitId || '',
      managerId: emp.managerId || '',
      joiningDate: emp.joiningDate || ''
    });
    setEditError('');
    setEditSuccess('');
  };

  const handleEditInputChange = (e: React.ChangeEvent<HTMLInputElement> | React.ChangeEvent<HTMLSelectElement>) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  const handleEditSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (editingId == null) return;
    setEditLoading(true);
    setEditError('');
    setEditSuccess('');
    try {
      await axios.put(`/employees/single?id=${editingId}`, editForm);
      setEditSuccess('Employee updated successfully');
      setEditingId(null);
      setEditForm(null);
      fetchEmployees();
    } catch (err: any) {
      setEditError('Failed to update employee');
    } finally {
      setEditLoading(false);
    }
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditForm(null);
    setEditError('');
    setEditSuccess('');
  };

  const handlePageChange = (newPage: number) => {
    fetchEmployees(newPage, pageSize);
  };

  const handlePageSizeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const newSize = parseInt(e.target.value, 10);
    setPageSize(newSize);
    fetchEmployees(0, newSize);
  };

  if (loading) return <div>Loading employees...</div>;
  return (
    <div style={{ width: '100vw', minHeight: '100vh', display: 'block' }}>
      <div style={{ maxWidth: 850, width: '100%', padding: 20, background: '#f5f7fa', borderRadius: 12, boxShadow: '0 4px 16px #e0e0e0', display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '32px 0 0 32px' }}>
        <h2 style={{ textAlign: 'center', marginBottom: 24, fontSize: '1.7rem', fontWeight: 600 }}>Employee Management</h2>
        {error && <div style={{ color: 'red', marginBottom: 10, fontSize: '1rem', textAlign: 'center' }}>{error}</div>}
        {success && <div style={{ color: 'green', marginBottom: 10, fontSize: '1rem', textAlign: 'center' }}>{success}</div>}
        <form onSubmit={handleCreate} style={{ width: '100%', minWidth: 800, display: 'flex', flexWrap: 'wrap', gap: 14, marginBottom: 24, alignItems: 'center', background: '#fff', padding: 14, borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', fontSize: '1rem', justifyContent: 'center' }}>
          <input name="firstName" placeholder="First Name" value={form.firstName} onChange={handleInputChange} required style={{ flex: '1 1 140px', fontSize: '1rem', padding: 8 }} />
          <input name="lastName" placeholder="Last Name" value={form.lastName} onChange={handleInputChange} required style={{ flex: '1 1 140px', fontSize: '1rem', padding: 8 }} />
          <input name="email" placeholder="Email" value={form.email} onChange={handleInputChange} required style={{ flex: '2 1 220px', fontSize: '1rem', padding: 8 }} />
          <select name="role" value={form.role} onChange={handleInputChange} required style={{ flex: '1 1 140px', fontSize: '1rem', padding: 8 }}>
            <option value="employee">Employee</option>
            <option value="teamleader">Teamleader</option>
            <option value="manager">Manager</option>
          </select>
          <input name="unitId" placeholder="Unit ID" value={form.unitId} onChange={handleInputChange} style={{ flex: '1 1 100px', fontSize: '1rem', padding: 8 }} />
          <input name="managerId" placeholder="Manager ID" value={form.managerId} onChange={handleInputChange} style={{ flex: '1 1 100px', fontSize: '1rem', padding: 8 }} />
          <input name="joiningDate" placeholder="Joining Date (YYYY-MM-DD)" value={form.joiningDate} onChange={handleInputChange} style={{ flex: '1 1 160px', fontSize: '1rem', padding: 8 }} />
          <div style={{ flex: '1 1 100%', display: 'flex', justifyContent: 'center', gap: 16 }}>
            <button type="submit" disabled={creating} style={{ background: '#1976d2', color: '#fff', border: 'none', borderRadius: 6, padding: '10px 24px', fontSize: '1rem', cursor: 'pointer', fontWeight: 500 }}>Create</button>
          </div>
        </form>
        <div style={{ marginBottom: 18, display: 'flex', alignItems: 'center', gap: 18, fontSize: '1rem', justifyContent: 'center', width: '100%', minWidth: 800 }}>
          <label>Page Size:</label>
          <select value={pageSize} onChange={handlePageSizeChange} style={{ padding: 6, fontSize: '1rem' }}>
            {[5, 10, 20, 50, 100].map(size => (
              <option key={size} value={size}>{size}</option>
            ))}
          </select>
          <span style={{ marginLeft: 24 }}>
            Page: <b>{page + 1}</b> / <b>{totalPages}</b>
          </span>
          <div style={{ display: 'flex', gap: 12 }}>
            <button onClick={() => handlePageChange(page - 1)} disabled={page <= 0} style={{ padding: '8px 20px', fontSize: '1rem', background: '#eee', border: '1px solid #ccc', borderRadius: 6, cursor: page <= 0 ? 'not-allowed' : 'pointer' }}>Prev</button>
            <button onClick={() => handlePageChange(page + 1)} disabled={page + 1 >= totalPages} style={{ padding: '8px 20px', fontSize: '1rem', background: '#eee', border: '1px solid #ccc', borderRadius: 6, cursor: page + 1 >= totalPages ? 'not-allowed' : 'pointer' }}>Next</button>
          </div>
        </div>
        <div style={{ width: '100%', overflowX: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse', background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', fontSize: '0.7rem', minWidth: 0 }}>
            <thead>
              <tr style={{ background: '#8da1bd' }}>
                <th style={{ padding: 12, borderBottom: '1px solid #e0e0e0', minWidth: 100, fontSize: '0.7rem' }}>First Name</th>
                <th style={{ padding: 12, borderBottom: '1px solid #e0e0e0', minWidth: 100, fontSize: '0.7rem' }}>Last Name</th>
                <th style={{ padding: 12, borderBottom: '1px solid #e0e0e0', minWidth: 140, fontSize: '0.7rem' }}>Email</th>
                <th style={{ padding: 12, borderBottom: '1px solid #e0e0e0', minWidth: 100, fontSize: '0.7rem' }}>Role</th>
                <th style={{ padding: 12, borderBottom: '1px solid #e0e0e0', minWidth: 60, fontSize: '0.7rem' }}>Unit ID</th>
                <th style={{ padding: 12, borderBottom: '1px solid #e0e0e0', minWidth: 60, fontSize: '0.7rem' }}>Manager ID</th>
                <th style={{ padding: 12, borderBottom: '1px solid #e0e0e0', minWidth: 100, fontSize: '0.7rem' }}>Joining Date</th>
                <th style={{ padding: 12, borderBottom: '1px solid #e0e0e0', minWidth: 120, fontSize: '0.7rem' }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {employees.map(emp => (
                <tr key={emp.id} style={{ background: editingId === emp.id ? '#e3f2fd' : '#fff' }}>
                  {editingId === emp.id ? (
                    <td colSpan={8} style={{ padding: 0 }}>
                      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minWidth: 900 }}>
                        <form onSubmit={handleEditSubmit} style={{ display: 'flex', gap: 12, alignItems: 'center', fontSize: '1rem', width: '100%', justifyContent: 'center' }}>
                          <input name="firstName" value={editForm.firstName} onChange={handleEditInputChange} required style={{ minWidth: 120, fontSize: '1rem', padding: 8 }} />
                          <input name="lastName" value={editForm.lastName} onChange={handleEditInputChange} required style={{ minWidth: 120, fontSize: '1rem', padding: 8 }} />
                          <input name="email" value={editForm.email} onChange={handleEditInputChange} required style={{ minWidth: 140, fontSize: '1rem', padding: 8 }} />
                          <select name="role" value={editForm.role} onChange={handleEditInputChange} required style={{ minWidth: 120, fontSize: '1rem', padding: 8 }}>
                            <option value="employee">Employee</option>
                            <option value="teamleader">Teamleader</option>
                            <option value="manager">Manager</option>
                          </select>
                          <input name="unitId" value={editForm.unitId} onChange={handleEditInputChange} style={{ minWidth: 60, fontSize: '1rem', padding: 8 }} />
                          <input name="managerId" value={editForm.managerId} onChange={handleEditInputChange} style={{ minWidth: 60, fontSize: '1rem', padding: 8 }} />
                          <input name="joiningDate" value={editForm.joiningDate} onChange={handleEditInputChange} style={{ minWidth: 100, fontSize: '1rem', padding: 8 }} />
                          <div style={{ display: 'flex', gap: 12 }}>
                            <button type="submit" disabled={editLoading} style={{ background: '#388e3c', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 20px', fontSize: '1rem', cursor: 'pointer', fontWeight: 500 }}>Save</button>
                            <button type="button" onClick={cancelEdit} disabled={editLoading} style={{ background: '#eee', color: '#333', border: 'none', borderRadius: 6, padding: '8px 20px', fontSize: '1rem', cursor: 'pointer' }}>Cancel</button>
                          </div>
                          {editError && <span style={{ color: 'red', marginLeft: 8 }}>{editError}</span>}
                          {editSuccess && <span style={{ color: 'green', marginLeft: 8 }}>{editSuccess}</span>}
                        </form>
                      </div>
                    </td>
                  ) : (
                    <>
                      <td style={{ padding: 12, minWidth: 100, fontSize: '0.7rem' }}>{emp.firstName}</td>
                      <td style={{ padding: 12, minWidth: 100, fontSize: '0.7rem' }}>{emp.lastName}</td>
                      <td style={{ padding: 12, minWidth: 140, fontSize: '0.7rem' }}>{emp.email}</td>
                      <td style={{ padding: 12, minWidth: 100, fontSize: '0.7rem' }}>{emp.role}</td>
                      <td style={{ padding: 12, minWidth: 60, fontSize: '0.7rem' }}>{emp.unitId}</td>
                      <td style={{ padding: 12, minWidth: 60, fontSize: '0.7rem' }}>{emp.managerId}</td>
                      <td style={{ padding: 12, minWidth: 100, fontSize: '0.7rem' }}>{emp.joiningDate}</td>
                      <td style={{ padding: 12, minWidth: 120, fontSize: '0.7rem' }}>
                        <div style={{ display: 'flex', gap: 8 }}>
                          <button style={{ background: '#d32f2f', color: '#fff', border: 'none', borderRadius: 6, padding: '6px 16px', fontSize: '1rem', cursor: 'pointer', fontWeight: 500 }} onClick={() => handleDelete(emp.id)}>Delete</button>
                          <PromoteButton emp={emp} onPromote={fetchEmployees} />
                          <button style={{ background: '#1976d2', color: '#fff', border: 'none', borderRadius: 6, padding: '6px 16px', fontSize: '1rem', cursor: 'pointer', fontWeight: 500 }} onClick={() => startEdit(emp)}>Edit</button>
                        </div>
                      </td>
                    </>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

const PromoteButton: React.FC<{ emp: any; onPromote: () => void }> = ({ emp, onPromote }) => {
  const [promoting, setPromoting] = useState(false);
  const [newRole, setNewRole] = useState(emp.role);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handlePromote = async () => {
    setPromoting(true);
    setError('');
    setSuccess('');
    try {
      await axios.put(`/employees/single?id=${emp.id}`, { ...emp, role: newRole });
      setSuccess('Role updated');
      onPromote();
    } catch (err: any) {
      setError('Failed to promote');
    } finally {
      setPromoting(false);
    }
  };

  return (
    <span style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
      <select value={newRole} onChange={e => setNewRole(e.target.value)} disabled={promoting} style={{ fontSize: '1rem', padding: '4px 8px', borderRadius: 4 }}>
        <option value="employee">Employee</option>
        <option value="teamleader">Teamleader</option>
        <option value="manager">Manager</option>
      </select>
      <button onClick={handlePromote} disabled={promoting || newRole === emp.role} style={{ background: '#ffa000', color: '#fff', border: 'none', borderRadius: 6, padding: '6px 16px', fontSize: '1rem', cursor: 'pointer', fontWeight: 500 }}>Promote</button>
      {error && <span style={{ color: 'red', marginLeft: 4 }}>{error}</span>}
      {success && <span style={{ color: 'green', marginLeft: 4 }}>{success}</span>}
    </span>
  );
};

export default EmployeeManagement;
