import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PAGE_SIZE_OPTIONS = [5, 10, 20, 50, 100];

const sortableColumns = [
  { key: 'name', label: 'Name' },
  { key: 'points', label: 'Points' },
];

const ROLE_OPTIONS = [
  { value: '', label: 'All Roles' },
  { value: 'employee', label: 'Employee' },
  { value: 'manager', label: 'Manager' },
  { value: 'admin', label: 'Admin' },
];

const LeaderboardManagement: React.FC = () => {
  const [leaderboard, setLeaderboard] = useState<{ name: string; points: number; role?: string }[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(0);
  const [pageSize, setPageSize] = useState(20);
  const [totalPages, setTotalPages] = useState(1);
  const [sortField, setSortField] = useState<'name' | 'points'>('points');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  const [search, setSearch] = useState('');
  const [topN, setTopN] = useState('');
  const [role, setRole] = useState('');

  useEffect(() => {
    const fetchLeaderboard = async () => {
      setLoading(true);
      setError('');
      try {
        const res = await axios.get('/leaderboard?top=1000');
        setLeaderboard(res.data || []);
      } catch (err) {
        setError('Failed to fetch leaderboard');
        setLeaderboard([]);
      } finally {
        setLoading(false);
      }
    };
    fetchLeaderboard();
  }, []);

  // Filter and sort
  const getFilteredSorted = () => {
    let filtered = leaderboard;
    if (role) {
      filtered = filtered.filter((e: { name: string; points: number; role?: string }) => e.role === role);
    }
    if (search.trim()) {
      const q = search.trim().toLowerCase();
      filtered = filtered.filter((e: { name: string; points: number; role?: string }) =>
        [e.name, e.points].some(val =>
          val !== undefined && val !== null && String(val).toLowerCase().includes(q)
        )
      );
    }
    filtered = [...filtered].sort((a, b) => {
      let aVal = a[sortField];
      let bVal = b[sortField];
      if (sortField === 'points') {
        aVal = Number(aVal);
        bVal = Number(bVal);
        if (aVal < bVal) return sortOrder === 'asc' ? -1 : 1;
        if (aVal > bVal) return sortOrder === 'asc' ? 1 : -1;
        return 0;
      } else {
        aVal = String(aVal).toLowerCase();
        bVal = String(bVal).toLowerCase();
        if (aVal < bVal) return sortOrder === 'asc' ? -1 : 1;
        if (aVal > bVal) return sortOrder === 'asc' ? 1 : -1;
        return 0;
      }
    });
    const n = parseInt(topN, 10);
    if (!isNaN(n) && n > 0) filtered = filtered.slice(0, n);
    return filtered;
  };

  const getPaged = () => {
    const filtered = getFilteredSorted();
    const total = Math.max(1, Math.ceil(filtered.length / pageSize));
    if (totalPages !== total) setTotalPages(total);
    return filtered.slice(page * pageSize, (page + 1) * pageSize);
  };

  useEffect(() => { setPage(0); }, [search, topN, pageSize]);

  const handlePageChange = (newPage: number) => setPage(newPage);
  const handlePageSizeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setPageSize(parseInt(e.target.value, 10));
    setPage(0);
  };

  return (
    <div style={{ width: '100%', overflowX: 'auto', minHeight: 400 }}>
      <h3 style={{ textAlign: 'center', fontSize: '1.2rem', fontWeight: 700, marginBottom: 18, letterSpacing: 1 }}>Leaderboard</h3>
      <section style={{
        marginBottom: 18,
        padding: 16,
        background: '#eaf0fa',
        border: '2px solid #8da1bd',
        borderRadius: 10,
        boxShadow: '0 2px 8px #e0e0e0',
        display: 'flex',
        flexWrap: 'wrap',
        gap: 18,
        alignItems: 'center',
        justifyContent: 'space-between',
        position: 'sticky',
        top: 0,
        zIndex: 10
      }}>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 10, minWidth: 220 }}>
          <label htmlFor="topN" style={{ fontWeight: 600, marginBottom: 4 }}>Show Top N:</label>
          <input
            id="topN"
            type="number"
            min={1}
            value={topN}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setTopN(e.target.value.replace(/[^\d]/g, ''))}
            placeholder="All"
            style={{ padding: 7, fontSize: '1rem', borderRadius: 6, border: '2px solid #8da1bd', background: '#fff', width: '100%' }}
          />
        </div>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 10, minWidth: 220 }}>
          <label htmlFor="role" style={{ fontWeight: 600, marginBottom: 4 }}>Role:</label>
          <select
            id="role"
            value={role}
            onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setRole(e.target.value)}
            style={{ padding: 7, fontSize: '1rem', borderRadius: 6, border: '2px solid #8da1bd', background: '#fff', width: '100%' }}
          >
            {ROLE_OPTIONS.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 10, minWidth: 220 }}>
          <label htmlFor="search" style={{ fontWeight: 600, marginBottom: 4 }}>Search:</label>
          <input
            id="search"
            type="text"
            value={search}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearch(e.target.value)}
            placeholder="Search leaderboard..."
            style={{ padding: 7, fontSize: '1rem', borderRadius: 6, border: '2px solid #8da1bd', background: '#fff', width: '100%' }}
          />
        </div>
      </section>
      <div style={{ marginBottom: 18, display: 'flex', alignItems: 'center', gap: 18, fontSize: '0.9rem', justifyContent: 'center', width: '100%' }}>
        <label style={{ fontWeight: 600 }}>Page Size:</label>
        <select value={pageSize} onChange={handlePageSizeChange} style={{ padding: 7, fontSize: '1rem', border: '2px solid #8da1bd', background: '#fff', borderRadius: 6 }}>
          {PAGE_SIZE_OPTIONS.map(size => (
            <option key={size} value={size}>{size}</option>
          ))}
        </select>
        <span style={{ marginLeft: 18 }}>
          Page: <b>{page + 1}</b> / <b>{totalPages}</b>
        </span>
        <div style={{ display: 'flex', gap: 10 }}>
          <button onClick={() => handlePageChange(page - 1)} disabled={page <= 0} style={{ padding: '7px 16px', fontSize: '1rem', background: '#eee', border: '2px solid #8da1bd', borderRadius: 6, cursor: page <= 0 ? 'not-allowed' : 'pointer', fontWeight: 600 }}>Prev</button>
          <button onClick={() => handlePageChange(page + 1)} disabled={page + 1 >= totalPages} style={{ padding: '7px 16px', fontSize: '1rem', background: '#eee', border: '2px solid #8da1bd', borderRadius: 6, cursor: page + 1 >= totalPages ? 'not-allowed' : 'pointer', fontWeight: 600 }}>Next</button>
        </div>
      </div>
      <table style={{ width: '100%', fontSize: '1rem', background: '#fff', borderRadius: 10, boxShadow: '0 2px 8px #e0e0e0', border: '2px solid #8da1bd' }}>
        <thead style={{ position: 'sticky', top: 120, zIndex: 1, background: '#8da1bd' }}>
          <tr>
            {sortableColumns.map(col => (
              <th
                key={col.key}
                style={{ padding: 12, cursor: 'pointer', userSelect: 'none', whiteSpace: 'nowrap', textAlign: 'left', fontWeight: 700, background: '#8da1bd', borderBottom: '2px solid #fff', fontSize: '1rem' }}
                onClick={() => {
                  if (sortField === col.key) {
                    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
                  } else {
                    setSortField(col.key as 'name' | 'points');
                    setSortOrder('asc');
                  }
                }}
              >
                {col.label}
                <span style={{ marginLeft: 6, color: sortField === col.key ? '#000' : '#bbb', fontWeight: 700, fontSize: '1.2rem', cursor: 'pointer' }}>
                  {sortField === col.key ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}
                </span>
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {getPaged().length === 0 ? (
            <tr><td colSpan={2} style={{ padding: 14, textAlign: 'center', color: '#888', fontSize: '1rem' }}>No leaderboard entries found. (Demo row: controls above)</td></tr>
          ) : getPaged().map((entry, idx) => (
            <tr key={idx} style={{ background: '#fff' }}>
              <td style={{ padding: 12 }}>{entry.name}</td>
              <td style={{ padding: 12 }}>{entry.points}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {loading && (
        <div style={{ fontSize: '1rem', textAlign: 'center', margin: 18 }}>Loading leaderboard...</div>
      )}
      {error && (
        <div style={{ color: 'red', fontSize: '1rem', textAlign: 'center', margin: 18 }}>{error}</div>
      )}
    </div>
  );
};

export default LeaderboardManagement;
