import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RecognitionManagement: React.FC = () => {
  const [recognitions, setRecognitions] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRecognitions = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/recognitions', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setRecognitions(res.data.content || []);
      } catch (err: any) {
        setError('Failed to fetch recognitions');
      } finally {
        setLoading(false);
      }
    };
    fetchRecognitions();
  }, []);

  if (loading) return <div>Loading recognitions...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h3>Recognition Management</h3>
      <ul>
        {recognitions.map(rec => (
          <li key={rec.id}>{rec.category} - {rec.level} ({rec.awardPoints} pts)</li>
        ))}
      </ul>
    </div>
  );
};

export default RecognitionManagement;
