import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EmployeeManagement: React.FC = () => {
  const [employees, setEmployees] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    email: '',
    role: 'employee',
    unitId: '',
    managerId: '',
    joiningDate: ''
  });
  const [creating, setCreating] = useState(false);
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editForm, setEditForm] = useState<any>(null);
  const [editLoading, setEditLoading] = useState(false);
  const [editError, setEditError] = useState('');
  const [editSuccess, setEditSuccess] = useState('');
  const [page, setPage] = useState(0);
  const [pageSize, setPageSize] = useState(20);
  const [totalPages, setTotalPages] = useState(1);

  const fetchEmployees = async (newPage = page, newPageSize = pageSize) => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get(`/employees?page=${newPage}&size=${newPageSize}`);
      setEmployees(res.data.content || []);
      setTotalPages(res.data.totalPages || 1);
      setPage(res.data.pageable?.pageNumber ?? newPage);
      setPageSize(res.data.pageable?.pageSize ?? newPageSize);
    } catch (err: any) {
      setError('Failed to fetch employees');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement> | React.ChangeEvent<HTMLSelectElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCreate = async (e: React.FormEvent) => {
    e.preventDefault();
    setCreating(true);
    setError('');
    setSuccess('');
    try {
      await axios.post('/employees', form);
      setSuccess('Employee created successfully');
      setForm({ firstName: '', lastName: '', email: '', role: 'employee', unitId: '', managerId: '', joiningDate: '' });
      fetchEmployees();
    } catch (err: any) {
      setError('Failed to create employee');
    } finally {
      setCreating(false);
    }
  };

  const handleDelete = async (id: number) => {
    setError('');
    setSuccess('');
    try {
      await axios.delete(`/employees/single?id=${id}`);
      setSuccess('Employee deleted successfully');
      fetchEmployees();
    } catch (err: any) {
      setError('Failed to delete employee');
    }
  };

  const startEdit = (emp: any) => {
    setEditingId(emp.id);
    setEditForm({
      firstName: emp.firstName || '',
      lastName: emp.lastName || '',
      email: emp.email || '',
      role: emp.role || 'employee',
      unitId: emp.unitId || '',
      managerId: emp.managerId || '',
      joiningDate: emp.joiningDate || ''
    });
    setEditError('');
    setEditSuccess('');
  };

  const handleEditInputChange = (e: React.ChangeEvent<HTMLInputElement> | React.ChangeEvent<HTMLSelectElement>) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  const handleEditSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (editingId == null) return;
    setEditLoading(true);
    setEditError('');
    setEditSuccess('');
    try {
      await axios.put(`/employees/single?id=${editingId}`, editForm);
      setEditSuccess('Employee updated successfully');
      setEditingId(null);
      setEditForm(null);
      fetchEmployees();
    } catch (err: any) {
      setEditError('Failed to update employee');
    } finally {
      setEditLoading(false);
    }
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditForm(null);
    setEditError('');
    setEditSuccess('');
  };

  const handlePageChange = (newPage: number) => {
    fetchEmployees(newPage, pageSize);
  };

  const handlePageSizeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const newSize = parseInt(e.target.value, 10);
    setPageSize(newSize);
    fetchEmployees(0, newSize);
  };

  if (loading) return <div>Loading employees...</div>;
  return (
    <div>
      <h3>Employee Management</h3>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {success && <div style={{ color: 'green' }}>{success}</div>}
      <form onSubmit={handleCreate} style={{ marginBottom: 16 }}>
        <input name="firstName" placeholder="First Name" value={form.firstName} onChange={handleInputChange} required />
        <input name="lastName" placeholder="Last Name" value={form.lastName} onChange={handleInputChange} required />
        <input name="email" placeholder="Email" value={form.email} onChange={handleInputChange} required />
        <select name="role" value={form.role} onChange={handleInputChange} required>
          <option value="employee">Employee</option>
          <option value="teamleader">Teamleader</option>
          <option value="manager">Manager</option>
        </select>
        <input name="unitId" placeholder="Unit ID" value={form.unitId} onChange={handleInputChange} />
        <input name="managerId" placeholder="Manager ID" value={form.managerId} onChange={handleInputChange} />
        <input name="joiningDate" placeholder="Joining Date (YYYY-MM-DD)" value={form.joiningDate} onChange={handleInputChange} />
        <button type="submit" disabled={creating}>Create Employee</button>
      </form>
      <div style={{ marginBottom: 16 }}>
        <label>Page Size: </label>
        <select value={pageSize} onChange={handlePageSizeChange}>
          {[5, 10, 20, 50, 100].map(size => (
            <option key={size} value={size}>{size}</option>
          ))}
        </select>
        <span style={{ marginLeft: 16 }}>
          Page: {page + 1} / {totalPages}
        </span>
        <button onClick={() => handlePageChange(page - 1)} disabled={page <= 0}>Prev</button>
        <button onClick={() => handlePageChange(page + 1)} disabled={page + 1 >= totalPages}>Next</button>
      </div>
      <ul>
        {employees.map(emp => (
          <li key={emp.id}>
            {editingId === emp.id ? (
              <form onSubmit={handleEditSubmit} style={{ display: 'inline-block', marginBottom: 8 }}>
                <input name="firstName" value={editForm.firstName} onChange={handleEditInputChange} required />
                <input name="lastName" value={editForm.lastName} onChange={handleEditInputChange} required />
                <input name="email" value={editForm.email} onChange={handleEditInputChange} required />
                <select name="role" value={editForm.role} onChange={handleEditInputChange} required>
                  <option value="employee">Employee</option>
                  <option value="teamleader">Teamleader</option>
                  <option value="manager">Manager</option>
                </select>
                <input name="unitId" value={editForm.unitId} onChange={handleEditInputChange} />
                <input name="managerId" value={editForm.managerId} onChange={handleEditInputChange} />
                <input name="joiningDate" value={editForm.joiningDate} onChange={handleEditInputChange} />
                <button type="submit" disabled={editLoading}>Save</button>
                <button type="button" onClick={cancelEdit} disabled={editLoading}>Cancel</button>
                {editError && <span style={{ color: 'red', marginLeft: 4 }}>{editError}</span>}
                {editSuccess && <span style={{ color: 'green', marginLeft: 4 }}>{editSuccess}</span>}
              </form>
            ) : (
              <>
                {emp.firstName} {emp.lastName} ({emp.role})
                <button style={{ marginLeft: 8 }} onClick={() => handleDelete(emp.id)}>Delete</button>
                <PromoteButton emp={emp} onPromote={fetchEmployees} />
                <button style={{ marginLeft: 8 }} onClick={() => startEdit(emp)}>Edit</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

const PromoteButton: React.FC<{ emp: any; onPromote: () => void }> = ({ emp, onPromote }) => {
  const [promoting, setPromoting] = useState(false);
  const [newRole, setNewRole] = useState(emp.role);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handlePromote = async () => {
    setPromoting(true);
    setError('');
    setSuccess('');
    try {
      await axios.put(`/employees/single?id=${emp.id}`, { ...emp, role: newRole });
      setSuccess('Role updated');
      onPromote();
    } catch (err: any) {
      setError('Failed to promote');
    } finally {
      setPromoting(false);
    }
  };

  return (
    <span style={{ marginLeft: 8 }}>
      <select value={newRole} onChange={e => setNewRole(e.target.value)} disabled={promoting}>
        <option value="employee">Employee</option>
        <option value="teamleader">Teamleader</option>
        <option value="manager">Manager</option>
      </select>
      <button onClick={handlePromote} disabled={promoting || newRole === emp.role}>Promote</button>
      {error && <span style={{ color: 'red', marginLeft: 4 }}>{error}</span>}
      {success && <span style={{ color: 'green', marginLeft: 4 }}>{success}</span>}
    </span>
  );
};

export default EmployeeManagement;
