import React from 'react';
import { useAuth } from '../services/auth';

const TeamRecognitions: React.FC = () => {
  const { user } = useAuth();
  const [teamRecs, setTeamRecs] = React.useState<any[]>([]);

  React.useEffect(() => {
    if (!user) return;
    // Assume teamleadId is user.email
    fetch(`/recognitions?teamleadEmail=${user.email}`)
      .then(res => res.json())
      .then(data => setTeamRecs(Array.isArray(data) ? data : data.content || []));
  }, [user]);

  return (
    <div style={{ padding: 24 }}>
      <h2>Team Recognitions</h2>
      <table style={{ width: '100%' }}>
        <thead>
          <tr><th>Sender</th><th>Recipient</th><th>Message</th><th>Points</th><th>Date</th></tr>
        </thead>
        <tbody>
          {teamRecs.length === 0 ? <tr><td colSpan={5}>No team recognitions found.</td></tr> : teamRecs.map((rec, i) => (
            <tr key={i}>
              <td>{rec.senderName || '-'}</td>
              <td>{rec.recipientName || '-'}</td>
              <td>{rec.message || '-'}</td>
              <td>{rec.awardPoints || '-'}</td>
              <td>{rec.sentAt ? new Date(rec.sentAt * 1000).toLocaleDateString() : '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TeamRecognitions;
