import React from 'react';

function getUserRoles(): string[] {
  const token = localStorage.getItem('token');
  if (!token) return [];
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.roles || payload.role || [];
  } catch {
    return [];
  }
}

const RecognitionsPage: React.FC = () => {
  const roles = getUserRoles();
  const canSend = roles.includes('EMPLOYEE');
  const canManage = roles.includes('ADMIN') || roles.includes('MANAGER') || roles.includes('TEAMLEADER');

  return (
    <div style={{ width: '100vw', minHeight: '100vh', display: 'block' }}>
      <div style={{ maxWidth: 850, width: '100%', padding: 10, background: '#f5f7fa', borderRadius: 12, boxShadow: '0 4px 16px #e0e0e0', display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '32px 0 0 32px' }}>
        <h2 style={{ textAlign: 'center', marginBottom: 18, fontSize: '1.3rem', fontWeight: 600 }}>Recognitions</h2>
        {canSend && <p style={{ fontSize: '0.7rem' }}>You can send recognitions.</p>}
        {canManage && <p style={{ fontSize: '0.7rem' }}>You can manage recognitions for your team/unit.</p>}
        {!canSend && !canManage && (
          <p style={{ color: 'red', fontSize: '0.7rem' }}>You do not have permission to send or manage recognitions.</p>
        )}
        {/* Table and actions for recognitions will go here */}
      </div>
    </div>
  );
};

export default RecognitionsPage;
