import React from 'react';
import EmployeeManagement from '../features/EmployeeManagement';
import RecognitionManagement from '../features/RecognitionManagement';
import RecognitionTypeManagement from '../features/RecognitionTypeManagement';

function getUserRoles(): string[] {
  const token = localStorage.getItem('token');
  if (!token) return [];
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.roles || payload.role || [];
  } catch {
    return [];
  }
}

const featureMap: Record<string, string[]> = {
  ADMIN: [
    'Manage all employees',
    'Manage all recognitions',
    'Manage recognition types',
    'View metrics and graphs',
    'Export data',
    'Admin controls'
  ],
  MANAGER: [
    'Manage employees in your unit',
    'Manage recognitions in your unit',
    'Manage recognition types',
    'View metrics and graphs',
    'Export data'
  ],
  TEAMLEADER: [
    'Manage employees in your team',
    'Manage recognitions in your team',
    'View metrics and graphs',
    'Export data'
  ],
  EMPLOYEE: [
    'Send recognitions',
    'View your recognitions',
    'View leaderboard'
  ]
};

const Dashboard: React.FC = () => {
  const roles = getUserRoles();
  return (
    <div style={{ width: '100vw', minHeight: '100vh', display: 'block' }}>
      <div style={{ maxWidth: 850, width: '100%', padding: 10, background: '#f5f7fa', borderRadius: 12, boxShadow: '0 4px 16px #e0e0e0', display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '32px 0 0 32px' }}>
        <h2 style={{ textAlign: 'center', marginBottom: 18, fontSize: '1.3rem', fontWeight: 600 }}>Dashboard</h2>
        <div style={{ fontSize: '0.7rem', marginBottom: 12 }}>
          <b>Your roles:</b> {roles.join(', ') || 'None'}
        </div>
        <ul style={{ fontSize: '0.7rem', width: '100%', padding: 0, margin: 0, listStyle: 'none' }}>
          {roles.map(role => (
            featureMap[role]?.map((feature, idx) => (
              <li key={role + idx} style={{ padding: '6px 0', borderBottom: '1px solid #e0e0e0', color: '#333' }}>{feature}</li>
            ))
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
