import React from 'react';
import { useAuth } from '../services/auth';

const cardStyle = {
  background: '#fff',
  borderRadius: 10,
  boxShadow: '0 2px 8px #e0e0e0',
  padding: 24,
  marginBottom: 32,
  maxWidth: 900,
  marginLeft: 'auto',
  marginRight: 'auto',
};

const tableStyle = {
  width: '100%',
  fontSize: '0.95rem',
  background: '#f9f9fb',
  borderRadius: 8,
  marginBottom: 24,
  borderCollapse: 'separate',
  borderSpacing: 0,
};

const thStyle = {
  background: '#8da1bd',
  color: '#fff',
  padding: 10,
  textAlign: 'left',
  fontWeight: 600,
  borderTopLeftRadius: 8,
  borderTopRightRadius: 8,
};

const tdStyle = {
  padding: 10,
  borderBottom: '1px solid #e0e0e0',
};

const UnitRecognitions: React.FC = () => {
  const { user } = useAuth();
  const [unitRecs, setUnitRecs] = React.useState<any[]>([]);

  React.useEffect(() => {
    if (!user) return;
    fetch(`/recognitions?unitId=${user.unitId}`)
      .then(res => res.json())
      .then(data => setUnitRecs(Array.isArray(data) ? data : data.content || []));
  }, [user]);

  return (
    <div style={{ padding: 32, background: '#f5f7fa', minHeight: '100vh' }}>
      <div style={cardStyle}>
        <h1 style={{ textAlign: 'center', marginBottom: 32 }}>Unit Recognitions</h1>
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={thStyle}>Sender</th>
              <th style={thStyle}>Recipient</th>
              <th style={thStyle}>Message</th>
              <th style={thStyle}>Points</th>
              <th style={thStyle}>Date</th>
            </tr>
          </thead>
          <tbody>
            {unitRecs.length === 0 ? <tr><td colSpan={5} style={tdStyle}>No unit recognitions found.</td></tr> : unitRecs.map((rec, i) => (
              <tr key={i}>
                <td style={tdStyle}>{rec.senderName || '-'}</td>
                <td style={tdStyle}>{rec.recipientName || '-'}</td>
                <td style={tdStyle}>{rec.message || '-'}</td>
                <td style={tdStyle}>{rec.awardPoints || '-'}</td>
                <td style={tdStyle}>{rec.sentAt ? new Date(rec.sentAt * 1000).toLocaleDateString() : '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default UnitRecognitions;
