import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Sidebar from '../components/Sidebar';
import { Bar, Line, Pie } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, PointElement, LineElement, ArcElement, Tooltip, Legend } from 'chart.js';
Chart.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, ArcElement, Tooltip, Legend);

const SIDEBAR_WIDTH = 180;

const Dashboard: React.FC = () => {
  // Metrics
  const [metrics, setMetrics] = useState({
    totalRecognitions: 0,
    totalEmployees: 0,
    activeUsers: [],
    topPerformer: '',
  });
  // Recent activity
  const [recentRecognitions, setRecentRecognitions] = useState<any[]>([]);
  // Chart data
  const [barData, setBarData] = useState<any>(null);
  const [lineData, setLineData] = useState<any>(null);
  const [pieData, setPieData] = useState<any>(null);
  const [metricsLoading, setMetricsLoading] = useState(true);

  useEffect(() => {
    // Fetch metrics
    axios.get('/metrics/summary').then(res => {
      // Example: extract totals from response
      const totals = res.data.totals || {};
      setMetrics({
        totalRecognitions: totals.count || 0,
        totalEmployees: 0, // You may need a separate endpoint for employees
        activeUsers: [], // Not available in metrics summary
        topPerformer: '', // Not available in metrics summary
      });
    }).catch(() => {
      setMetrics({ totalRecognitions: 0, totalEmployees: 0, activeUsers: [], topPerformer: '' });
    });
    // Fetch recent recognitions (use /recognitions?page=0&size=10)
    axios.get('/recognitions?page=0&size=10').then(res => {
      setRecentRecognitions(res.data);
    }).catch(() => {
      setRecentRecognitions([]);
    });
    // Fetch leaderboard data
    axios.get('/leaderboard/top-senders?page=0&size=10').then(() => {
      // Removed teamChartData usage
    }).catch(() => {
      // Removed teamChartData usage
    });
    // Fetch recognitions for metrics charts
    setMetricsLoading(true);
    axios.get('/recognitions?page=0&size=100').then(res => {
      const recognitions = Array.isArray(res.data) ? res.data : res.data.content || [];
      // Aggregate by month for bar/line
      const monthMap: Record<string, { count: number; points: number }> = {};
      const teamMap: Record<string, number> = {};
      recognitions.forEach((rec: any) => {
        let date = new Date((rec.sentAt && typeof rec.sentAt === 'number') ? rec.sentAt * 1000 : Date.now());
        let month = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
        monthMap[month] = monthMap[month] || { count: 0, points: 0 };
        monthMap[month].count += 1;
        monthMap[month].points += rec.awardPoints || 0;
        let team = rec.recipientRole || 'Unknown';
        teamMap[team] = (teamMap[team] || 0) + 1;
      });
      const months = Object.keys(monthMap).sort();
      setBarData({
        labels: months,
        datasets: [{ label: 'Recognitions Sent', data: months.map(m => monthMap[m].count), backgroundColor: '#1976d2' }],
      });
      setLineData({
        labels: months,
        datasets: [{ label: 'Points Awarded', data: months.map(m => monthMap[m].points), fill: false, borderColor: '#388e3c', tension: 0.1 }],
      });
      const teams = Object.keys(teamMap);
      setPieData({
        labels: teams,
        datasets: [{ label: 'Recognitions by Team', data: teams.map(t => teamMap[t]), backgroundColor: ['#1976d2', '#ffa000', '#388e3c', '#8da1bd', '#e57373', '#81c784'] }],
      });
      setMetricsLoading(false);
    }).catch(() => {
      setMetricsLoading(false);
      setBarData(null);
      setLineData(null);
      setPieData(null);
    });
  }, []);

  // Add safeDisplay helper outside the map callback
  function safeDisplay(val: any): string {
    if (val == null) return '-';
    if (typeof val === 'string' || typeof val === 'number') return String(val);
    if (Array.isArray(val)) {
      // Flatten and join only primitive strings/numbers
      return val.map(safeDisplay).filter(v => typeof v === 'string').join(', ');
    }
    if (React.isValidElement(val)) return '-';
    if (typeof val === 'object') {
      // If object has a displayable property
      if (val.firstName || val.lastName) {
        const name = `${val.firstName || ''} ${val.lastName || ''}`.trim();
        return name || '-';
      }
      if (val.name && typeof val.name === 'string') return val.name;
      // If object is a Date
      if (val instanceof Date && !isNaN(val.getTime())) return val.toLocaleDateString();
      // If object is not displayable, fallback
      return '-';
    }
    return '-';
  }

  return (
    <div style={{ width: '100vw', minHeight: '100vh', display: 'flex', background: '#f5f7fa' }}>
      <div style={{ width: SIDEBAR_WIDTH, minWidth: SIDEBAR_WIDTH, height: '100vh', position: 'relative', zIndex: 2 }}>
        <Sidebar />
      </div>
      <div style={{ flex: 1, padding: 32, display: 'flex', flexDirection: 'column', alignItems: 'center', background: '#f5f7fa', borderRadius: 12, boxShadow: '0 4px 16px #e0e0e0', minHeight: '100vh' }}>
        <h2 style={{ textAlign: 'center', marginBottom: 18, fontSize: '1.3rem', fontWeight: 600 }}>Dashboard</h2>
        {/* Metrics cards */}
        <div style={{ display: 'flex', gap: 24, marginBottom: 24, width: '100%', justifyContent: 'center' }}>
          <div style={{ background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', padding: 18, minWidth: 160, textAlign: 'center' }}>
            <div style={{ fontSize: '0.8rem', color: '#888' }}>Total Recognitions</div>
            <div style={{ fontSize: '1.2rem', fontWeight: 700 }}>{metrics.totalRecognitions}</div>
          </div>
          <div style={{ background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', padding: 18, minWidth: 160, textAlign: 'center' }}>
            <div style={{ fontSize: '0.8rem', color: '#888' }}>Total Employees</div>
            <div style={{ fontSize: '1.2rem', fontWeight: 700 }}>{metrics.totalEmployees}</div>
          </div>
          <div style={{ background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', padding: 18, minWidth: 160, textAlign: 'center' }}>
            <div style={{ fontSize: '0.8rem', color: '#888' }}>Active Users</div>
            <div style={{ fontSize: '1rem', fontWeight: 600 }}>{metrics.activeUsers.join(', ') || '-'}</div>
          </div>
          <div style={{ background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', padding: 18, minWidth: 160, textAlign: 'center' }}>
            <div style={{ fontSize: '0.8rem', color: '#888' }}>Top Performer</div>
            <div style={{ fontSize: '1rem', fontWeight: 600 }}>{metrics.topPerformer || '-'}</div>
          </div>
        </div>
        {/* Metrics charts section */}
        <div style={{ width: '100%', display: 'flex', gap: 32, justifyContent: 'center', marginBottom: 32 }}>
          {!metricsLoading && barData && (
            <div style={{ width: '100%', maxWidth: 400 }}>
              <h3 style={{ fontSize: '1rem', marginBottom: 8 }}>Recognitions Sent (Bar Chart)</h3>
              <Bar data={barData} options={{ plugins: { legend: { display: false } }}}
              />
            </div>
          )}
          {!metricsLoading && lineData && (
            <div style={{ width: '100%', maxWidth: 400 }}>
              <h3 style={{ fontSize: '1rem', marginBottom: 8 }}>Points Awarded (Line Chart)</h3>
              <Line data={lineData} />
            </div>
          )}
          {!metricsLoading && pieData && (
            <div style={{ width: '100%', maxWidth: 300 }}>
              <h3 style={{ fontSize: '1rem', marginBottom: 8 }}>Recognitions by Team (Pie Chart)</h3>
              <Pie data={pieData} />
            </div>
          )}
        </div>
        {/* Charts and tables */}
        <div style={{ display: 'flex', gap: 32, width: '100%', justifyContent: 'center', alignItems: 'flex-start', marginBottom: 32 }}>
          {/* Line chart with local filters */}
          <div style={{ flex: 1, minWidth: 320, background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', padding: 18 }}>
            <div style={{ fontWeight: 600, marginBottom: 8 }}>Recognition Trends (Line Chart)</div>
            <div style={{ display: 'flex', gap: 12, marginBottom: 12 }}>
              <select style={{ padding: 6, borderRadius: 6, border: '1px solid #ccc', fontSize: '0.9rem' }}>
                <option value="employee">Employee</option>
                <option value="manager">Manager</option>
                <option value="teamlead">Team Lead</option>
              </select>
              <select style={{ padding: 6, borderRadius: 6, border: '1px solid #ccc', fontSize: '0.9rem' }}>
                <option value="team">Team</option>
                <option value="unit">Unit</option>
              </select>
            </div>
            {/* Chart.js or Recharts line chart here, using trendData */}
            <div style={{ height: 180, background: '#f5f7fa', borderRadius: 6, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#bbb' }}>[Line Chart Placeholder]</div>
          </div>
          {/* Pie chart with local filters */}
          <div style={{ flex: 1, minWidth: 320, background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', padding: 18 }}>
            <div style={{ fontWeight: 600, marginBottom: 8 }}>Recognitions by Role/Team/Unit (Pie Chart)</div>
            <div style={{ display: 'flex', gap: 12, marginBottom: 12 }}>
              <select style={{ padding: 6, borderRadius: 6, border: '1px solid #ccc', fontSize: '0.9rem' }}>
                <option value="employee">Employee</option>
                <option value="manager">Manager</option>
                <option value="teamlead">Team Lead</option>
              </select>
              <select style={{ padding: 6, borderRadius: 6, border: '1px solid #ccc', fontSize: '0.9rem' }}>
                <option value="team">Team</option>
                <option value="unit">Unit</option>
              </select>
            </div>
            {/* Chart.js or Recharts pie chart here, using pieData */}
            <div style={{ height: 180, background: '#f5f7fa', borderRadius: 6, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#bbb' }}>[Pie Chart Placeholder]</div>
          </div>
        </div>
        {/* Team members chart (role-based) with local filters */}
        {(metrics.activeUsers.length > 0) && (
          <div style={{ width: '100%', marginBottom: 32, background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', padding: 18 }}>
            <div style={{ fontWeight: 600, marginBottom: 8 }}>Team Members Performance</div>
            <div style={{ display: 'flex', gap: 12, marginBottom: 12 }}>
              <select style={{ padding: 6, borderRadius: 6, border: '1px solid #ccc', fontSize: '0.9rem' }}>
                <option value="employee">Employee</option>
                <option value="manager">Manager</option>
                <option value="teamlead">Team Lead</option>
              </select>
              <select style={{ padding: 6, borderRadius: 6, border: '1px solid #ccc', fontSize: '0.9rem' }}>
                <option value="team">Team</option>
                <option value="unit">Unit</option>
              </select>
            </div>
            {/* Chart/table for team members, using teamChartData */}
            <div style={{ height: 180, background: '#f5f7fa', borderRadius: 6, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#bbb' }}>[Team Chart/Table Placeholder]</div>
          </div>
        )}
        {/* Recent activity table */}
        <div style={{ width: '100%', background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', padding: 18 }}>
          <div style={{ fontWeight: 600, marginBottom: 8 }}>Recent Recognitions</div>
          <table style={{ width: '100%', fontSize: '0.8rem', background: '#fff', borderRadius: 8 }}>
            <thead>
              <tr style={{ background: '#8da1bd' }}>
                <th style={{ padding: 8, textAlign: 'left', fontWeight: 600 }}>Sender</th>
                <th style={{ padding: 8, textAlign: 'left', fontWeight: 600 }}>Receiver</th>
                <th style={{ padding: 8, textAlign: 'left', fontWeight: 600 }}>Type</th>
                <th style={{ padding: 8, textAlign: 'left', fontWeight: 600 }}>Date</th>
              </tr>
            </thead>
            <tbody>
              {recentRecognitions.length === 0 ? (
                <tr><td colSpan={4} style={{ padding: 8, textAlign: 'center', color: '#888' }}>No recent recognitions found.</td></tr>
              ) : recentRecognitions.map((row, idx) => {
                const sender = safeDisplay(row.sender);
                const receiver = safeDisplay(row.receiver);
                const type = safeDisplay(row.type);
                let date = safeDisplay(row.date);
                if (row.sentAt) {
                  try {
                    date = new Date(row.sentAt).toLocaleDateString();
                  } catch {
                    date = safeDisplay(row.sentAt);
                  }
                }
                // Guarantee only strings are rendered
                return (
                  <tr key={idx} style={{ background: idx % 2 === 0 ? '#f5f7fa' : '#fff' }}>
                    <td style={{ padding: 8 }}>{sender}</td>
                    <td style={{ padding: 8 }}>{receiver}</td>
                    <td style={{ padding: 8 }}>{type}</td>
                    <td style={{ padding: 8 }}>{date}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
