import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EmployeeManagement: React.FC = () => {
  const [employees, setEmployees] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    email: '',
    role: 'employee',
    unitId: '',
    managerId: '',
    joiningDate: ''
  });
  const [creating, setCreating] = useState(false);
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editForm, setEditForm] = useState<any>(null);
  const [editLoading, setEditLoading] = useState(false);
  const [editError, setEditError] = useState('');
  const [editSuccess, setEditSuccess] = useState('');
  const [page, setPage] = useState(0);
  const [pageSize, setPageSize] = useState(20);
  const [totalPages, setTotalPages] = useState(1);

  const fetchEmployees = async (newPage = page, newPageSize = pageSize) => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get(`/employees?page=${newPage}&size=${newPageSize}`);
      setEmployees(res.data.content || []);
      setTotalPages(res.data.totalPages || 1);
      setPage(res.data.pageable?.pageNumber ?? newPage);
      setPageSize(res.data.pageable?.pageSize ?? newPageSize);
    } catch (err: any) {
      setError('Failed to fetch employees');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement> | React.ChangeEvent<HTMLSelectElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCreate = async (e: React.FormEvent) => {
    e.preventDefault();
    setCreating(true);
    setError('');
    setSuccess('');
    try {
      await axios.post('/employees', form);
      setSuccess('Employee created successfully');
      setForm({ firstName: '', lastName: '', email: '', role: 'employee', unitId: '', managerId: '', joiningDate: '' });
      fetchEmployees();
    } catch (err: any) {
      setError('Failed to create employee');
    } finally {
      setCreating(false);
    }
  };

  const handleDelete = async (id: number) => {
    setError('');
    setSuccess('');
    try {
      await axios.delete(`/employees/single?id=${id}`);
      setSuccess('Employee deleted successfully');
      fetchEmployees();
    } catch (err: any) {
      setError('Failed to delete employee');
    }
  };

  const startEdit = (emp: any) => {
    setEditingId(emp.id);
    setEditForm({
      firstName: emp.firstName || '',
      lastName: emp.lastName || '',
      email: emp.email || '',
      role: emp.role || 'employee',
      unitId: emp.unitId || '',
      managerId: emp.managerId || '',
      joiningDate: emp.joiningDate || ''
    });
    setEditError('');
    setEditSuccess('');
  };

  const handleEditInputChange = (e: React.ChangeEvent<HTMLInputElement> | React.ChangeEvent<HTMLSelectElement>) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  const handleEditSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (editingId == null) return;
    setEditLoading(true);
    setEditError('');
    setEditSuccess('');
    try {
      await axios.put(`/employees/single?id=${editingId}`, editForm);
      setEditSuccess('Employee updated successfully');
      setEditingId(null);
      setEditForm(null);
      fetchEmployees();
    } catch (err: any) {
      setEditError('Failed to update employee');
    } finally {
      setEditLoading(false);
    }
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditForm(null);
    setEditError('');
    setEditSuccess('');
  };

  const handlePageChange = (newPage: number) => {
    fetchEmployees(newPage, pageSize);
  };

  const handlePageSizeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const newSize = parseInt(e.target.value, 10);
    setPageSize(newSize);
    fetchEmployees(0, newSize);
  };

  if (loading) return <div>Loading employees...</div>;
  return (
    <div style={{ maxWidth: 900, margin: '0 auto', padding: 24, background: '#fafafa', borderRadius: 8, boxShadow: '0 2px 8px #eee' }}>
      <h2 style={{ textAlign: 'center', marginBottom: 24 }}>Employee Management</h2>
      {error && <div style={{ color: 'red', marginBottom: 8 }}>{error}</div>}
      {success && <div style={{ color: 'green', marginBottom: 8 }}>{success}</div>}
      <form onSubmit={handleCreate} style={{ display: 'flex', flexWrap: 'wrap', gap: 12, marginBottom: 24, alignItems: 'center', background: '#fff', padding: 16, borderRadius: 6, boxShadow: '0 1px 4px #eee' }}>
        <input name="firstName" placeholder="First Name" value={form.firstName} onChange={handleInputChange} required style={{ flex: '1 1 120px' }} />
        <input name="lastName" placeholder="Last Name" value={form.lastName} onChange={handleInputChange} required style={{ flex: '1 1 120px' }} />
        <input name="email" placeholder="Email" value={form.email} onChange={handleInputChange} required style={{ flex: '2 1 180px' }} />
        <select name="role" value={form.role} onChange={handleInputChange} required style={{ flex: '1 1 120px' }}>
          <option value="employee">Employee</option>
          <option value="teamleader">Teamleader</option>
          <option value="manager">Manager</option>
        </select>
        <input name="unitId" placeholder="Unit ID" value={form.unitId} onChange={handleInputChange} style={{ flex: '1 1 80px' }} />
        <input name="managerId" placeholder="Manager ID" value={form.managerId} onChange={handleInputChange} style={{ flex: '1 1 80px' }} />
        <input name="joiningDate" placeholder="Joining Date (YYYY-MM-DD)" value={form.joiningDate} onChange={handleInputChange} style={{ flex: '1 1 140px' }} />
        <button type="submit" disabled={creating} style={{ flex: '0 1 120px', background: '#1976d2', color: '#fff', border: 'none', borderRadius: 4, padding: '8px 16px', cursor: 'pointer' }}>Create</button>
      </form>
      <div style={{ marginBottom: 16, display: 'flex', alignItems: 'center', gap: 16 }}>
        <label>Page Size:</label>
        <select value={pageSize} onChange={handlePageSizeChange} style={{ padding: 4 }}>
          {[5, 10, 20, 50, 100].map(size => (
            <option key={size} value={size}>{size}</option>
          ))}
        </select>
        <span style={{ marginLeft: 16 }}>
          Page: <b>{page + 1}</b> / <b>{totalPages}</b>
        </span>
        <button onClick={() => handlePageChange(page - 1)} disabled={page <= 0} style={{ marginLeft: 8, padding: '4px 12px' }}>Prev</button>
        <button onClick={() => handlePageChange(page + 1)} disabled={page + 1 >= totalPages} style={{ marginLeft: 4, padding: '4px 12px' }}>Next</button>
      </div>
      <table style={{ width: '100%', borderCollapse: 'collapse', background: '#fff', borderRadius: 6, boxShadow: '0 1px 4px #eee' }}>
        <thead>
          <tr style={{ background: '#f5f5f5' }}>
            <th style={{ padding: 8, borderBottom: '1px solid #eee' }}>First Name</th>
            <th style={{ padding: 8, borderBottom: '1px solid #eee' }}>Last Name</th>
            <th style={{ padding: 8, borderBottom: '1px solid #eee' }}>Email</th>
            <th style={{ padding: 8, borderBottom: '1px solid #eee' }}>Role</th>
            <th style={{ padding: 8, borderBottom: '1px solid #eee' }}>Unit ID</th>
            <th style={{ padding: 8, borderBottom: '1px solid #eee' }}>Manager ID</th>
            <th style={{ padding: 8, borderBottom: '1px solid #eee' }}>Joining Date</th>
            <th style={{ padding: 8, borderBottom: '1px solid #eee' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(emp => (
            <tr key={emp.id} style={{ background: editingId === emp.id ? '#e3f2fd' : '#fff' }}>
              {editingId === emp.id ? (
                <>
                  <td colSpan={8}>
                    <form onSubmit={handleEditSubmit} style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
                      <input name="firstName" value={editForm.firstName} onChange={handleEditInputChange} required style={{ flex: '1 1 120px' }} />
                      <input name="lastName" value={editForm.lastName} onChange={handleEditInputChange} required style={{ flex: '1 1 120px' }} />
                      <input name="email" value={editForm.email} onChange={handleEditInputChange} required style={{ flex: '2 1 180px' }} />
                      <select name="role" value={editForm.role} onChange={handleEditInputChange} required style={{ flex: '1 1 120px' }}>
                        <option value="employee">Employee</option>
                        <option value="teamleader">Teamleader</option>
                        <option value="manager">Manager</option>
                      </select>
                      <input name="unitId" value={editForm.unitId} onChange={handleEditInputChange} style={{ flex: '1 1 80px' }} />
                      <input name="managerId" value={editForm.managerId} onChange={handleEditInputChange} style={{ flex: '1 1 80px' }} />
                      <input name="joiningDate" value={editForm.joiningDate} onChange={handleEditInputChange} style={{ flex: '1 1 140px' }} />
                      <button type="submit" disabled={editLoading} style={{ background: '#388e3c', color: '#fff', border: 'none', borderRadius: 4, padding: '6px 14px', cursor: 'pointer' }}>Save</button>
                      <button type="button" onClick={cancelEdit} disabled={editLoading} style={{ background: '#eee', color: '#333', border: 'none', borderRadius: 4, padding: '6px 14px', cursor: 'pointer' }}>Cancel</button>
                      {editError && <span style={{ color: 'red', marginLeft: 4 }}>{editError}</span>}
                      {editSuccess && <span style={{ color: 'green', marginLeft: 4 }}>{editSuccess}</span>}
                    </form>
                  </td>
                </>
              ) : (
                <>
                  <td style={{ padding: 8 }}>{emp.firstName}</td>
                  <td style={{ padding: 8 }}>{emp.lastName}</td>
                  <td style={{ padding: 8 }}>{emp.email}</td>
                  <td style={{ padding: 8 }}>{emp.role}</td>
                  <td style={{ padding: 8 }}>{emp.unitId}</td>
                  <td style={{ padding: 8 }}>{emp.managerId}</td>
                  <td style={{ padding: 8 }}>{emp.joiningDate}</td>
                  <td style={{ padding: 8 }}>
                    <button style={{ marginRight: 6, background: '#d32f2f', color: '#fff', border: 'none', borderRadius: 4, padding: '4px 10px', cursor: 'pointer' }} onClick={() => handleDelete(emp.id)}>Delete</button>
                    <PromoteButton emp={emp} onPromote={fetchEmployees} />
                    <button style={{ marginLeft: 6, background: '#1976d2', color: '#fff', border: 'none', borderRadius: 4, padding: '4px 10px', cursor: 'pointer' }} onClick={() => startEdit(emp)}>Edit</button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const PromoteButton: React.FC<{ emp: any; onPromote: () => void }> = ({ emp, onPromote }) => {
  const [promoting, setPromoting] = useState(false);
  const [newRole, setNewRole] = useState(emp.role);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handlePromote = async () => {
    setPromoting(true);
    setError('');
    setSuccess('');
    try {
      await axios.put(`/employees/single?id=${emp.id}`, { ...emp, role: newRole });
      setSuccess('Role updated');
      onPromote();
    } catch (err: any) {
      setError('Failed to promote');
    } finally {
      setPromoting(false);
    }
  };

  return (
    <span style={{ marginLeft: 8 }}>
      <select value={newRole} onChange={e => setNewRole(e.target.value)} disabled={promoting}>
        <option value="employee">Employee</option>
        <option value="teamleader">Teamleader</option>
        <option value="manager">Manager</option>
      </select>
      <button onClick={handlePromote} disabled={promoting || newRole === emp.role}>Promote</button>
      {error && <span style={{ color: 'red', marginLeft: 4 }}>{error}</span>}
      {success && <span style={{ color: 'green', marginLeft: 4 }}>{success}</span>}
    </span>
  );
};

export default EmployeeManagement;
