import React, { useEffect, useState } from 'react';
import Sidebar from '../components/Sidebar';

const LeaderboardPage: React.FC = () => {
  const [leaderboard, setLeaderboard] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchLeaderboard = async () => {
      setLoading(true);
      setError('');
      try {
        const res = await fetch('/leaderboard');
        if (!res.ok) throw new Error('Failed to fetch leaderboard: ' + res.status + ' ' + res.statusText);
        const data = await res.json();
        setLeaderboard(data.content || data || []);
      } catch (err) {
        console.error('Leaderboard fetch error:', err);
        setError('Failed to fetch leaderboard from the backend. Please check if the backend is running and the endpoint is correct.');
        setLeaderboard([]);
      } finally {
        setLoading(false);
      }
    };
    fetchLeaderboard();
  }, []);

  return (
    <div style={{ width: '100vw', minHeight: '100vh', display: 'flex' }}>
      <Sidebar />
      <div style={{ maxWidth: 850, width: '100%', padding: 10, marginLeft: 180, background: '#f5f7fa', borderRadius: 12, boxShadow: '0 4px 16px #e0e0e0', display: 'flex', flexDirection: 'column', alignItems: 'center', margin: '32px 0 0 32px' }}>
        <h2 style={{ textAlign: 'center', marginBottom: 18, fontSize: '1.3rem', fontWeight: 600 }}>Leaderboard</h2>
        {loading && <div style={{ fontSize: '0.7rem' }}>Loading leaderboard...</div>}
        {error && <div style={{ color: 'red', fontSize: '0.7rem', marginBottom: 8 }}>{error}</div>}
        <table style={{ width: '100%', fontSize: '0.7rem', background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #e0e0e0', marginTop: 12 }}>
          <thead>
            <tr style={{ background: '#8da1bd' }}>
              <th style={{ padding: 8 }}>Name</th>
              <th style={{ padding: 8 }}>Points</th>
            </tr>
          </thead>
          <tbody>
            {leaderboard.map((row, idx) => (
              <tr key={row.name || row.id || idx} style={{ background: idx % 2 === 0 ? '#f5f7fa' : '#fff' }}>
                <td style={{ padding: 8 }}>
                  {typeof row.name === 'string' || typeof row.name === 'number'
                    ? row.name
                    : row.name && typeof row.name === 'object'
                      ? JSON.stringify(row.name)
                      : '-'}
                </td>
                <td style={{ padding: 8 }}>
                  {typeof row.points === 'string' || typeof row.points === 'number'
                    ? row.points
                    : row.points && typeof row.points === 'object'
                      ? JSON.stringify(row.points)
                      : '-'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {!loading && !error && leaderboard.length === 0 && (
          <div style={{ fontSize: '0.7rem', color: '#888', marginTop: 12 }}>No leaderboard data found.</div>
        )}
      </div>
    </div>
  );
};

export default LeaderboardPage;
